import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit {
  private heroes: Hero[] = [
    {
      id: 1,
      name: 'SPIDER-MAN',
      isMarvel: true,
      age: 15,
      img: 'data:image/jpeg;base64,',
    },
    {
      id: 2,
      name: 'CAPITÁN AMÉRICA',
      isMarvel: true,
      age: 39,
      img: 'https://www.cinemascomics.com/wp-content/uploads/2020/09/traicion-capitan-america.jpg',
    },
    {
      id: 3,
      name: 'IRON MAN',
      isMarvel: true,
      age: 53,
      img: 'data:image/jpeg;base64,',
    },
    {
      id: 4,
      name: 'THOR',
      isMarvel: true,
      age: 1.5,
      img: 'data:image/jpeg;base64,',
    },
    {
      id: 5,
      name: 'SUPERMAN',
      isMarvel: false,
      age: 80,
      img: 'https://media.revistagq.com/photos/5eaa92505a16590008bff7c4/master/pass/superman.jpg',
    },
    {
      id: 6,
      name: 'BATMAN',
      isMarvel: false,
      age: 60,
      img: 'https://elcomercio.pe/resizer/uBccTm-MPi9EMaYogG8EGtAgesQ=/1200x900/smart/filters:format(jpeg):quality(75)/cloudfront-us-east-1.images.arcpublishing.com/elcomercio/BSIPV333VJCDPB625ZKXNQWMZ4.jpg',
    },
    {
      id: 7,
      name: 'FLASH',
      isMarvel: false,
      age: 25,
      img: 'https://www.cinemascomics.com/wp-content/uploads/2016/11/Flash-liga-de-la-justicia-justice-league-trailer-e1485419301433.jpg',
    },
  ];

  public heroMarvel: Hero[] = [];
  public dcHeroes: Hero[] = [];

  ngOnInit(): void {
    this.heroMarvel = this.heroes.filter((hero) => hero.isMarvel);
    this.dcHeroes = this.heroes.filter((hero) => !hero.isMarvel);
  }

  changeDc(item: Hero) {
    item.isMarvel = false;
    this.filterHeroMarvel();
    this.dcHeroes.push(item);
  }
  changeMarvel(item: Hero) {
    item.isMarvel = true;
    this.filterHeroDc();
    this.heroMarvel.push(item);
  }
  public changeHeroTeam(hero: Hero): void {
    console.log('cambio de team', hero);
    hero.isMarvel ? this.changeDc(hero) : this.changeMarvel(hero);
  }

  filterHeroMarvel() {
    this.heroMarvel = this.heroMarvel.filter((hero) => hero.isMarvel);
  }
  filterHeroDc() {
    this.dcHeroes = this.dcHeroes.filter((hero) => !hero.isMarvel);
  }
}

export interface Hero {
  id: number;
  name: string;
  isMarvel: boolean;
  age: number;
  img: string;
}
